    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
        try {
            if (jComboBox1.getSelectedItem().toString().equalsIgnoreCase("Suma")) {
                jLabel4.setText(String.valueOf(Operaciones.sumar(Double.parseDouble(jTextField5.getText()), Double.parseDouble(jTextField6.getText()))));
            } else if (jComboBox1.getSelectedItem().toString().equalsIgnoreCase("Resta")) {
                jLabel4.setText(String.valueOf(Operaciones.restar(Double.parseDouble(jTextField5.getText()), Double.parseDouble(jTextField6.getText()))));
            } else if (jComboBox1.getSelectedItem().toString().equalsIgnoreCase("Multiplica")) {
                jLabel4.setText(String.valueOf(Operaciones.multiplicar(Double.parseDouble(jTextField5.getText()), Double.parseDouble(jTextField6.getText()))));
            } else {
                jLabel4.setText(String.valueOf(Operaciones.dividir(Double.parseDouble(jTextField5.getText()), Double.parseDouble(jTextField6.getText()))));
            }
        } catch (NumberFormatException e) {
            System.out.println("MAL");
        }
    }//GEN-LAST:event_jComboBox1ActionPerformed

