    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        try {
            if (jRadioButton1.isSelected()) {
                jLabel2.setText(String.valueOf(Operaciones.sumar(Double.parseDouble(jTextField3.getText()), Double.parseDouble(jTextField4.getText()))));
            } else if (jRadioButton2.isSelected()) {
                jLabel2.setText(String.valueOf(Operaciones.restar(Double.parseDouble(jTextField3.getText()), Double.parseDouble(jTextField4.getText()))));
            } else if (jRadioButton3.isSelected()) {
                jLabel2.setText(String.valueOf(Operaciones.multiplicar(Double.parseDouble(jTextField3.getText()), Double.parseDouble(jTextField4.getText()))));
            } else if (jRadioButton4.isSelected()) {
                jLabel2.setText(String.valueOf(Operaciones.dividir(Double.parseDouble(jTextField3.getText()), Double.parseDouble(jTextField4.getText()))));
            } else {

            }
        } catch (NumberFormatException e) {
            System.out.println("MAL");
        }
    }//GEN-LAST:event_jButton5ActionPerformed

